                                })}
                            />
                            {errors.email && (
                                <p className="error_mssg">{errors.email.message}</p>
                            )}
                        </label>




                        <label htmlFor="details-wachtwoord">
                            Wachtwoord :
                            <input
                                type="text"
                                id="password"
                                // value={password}
                                // onChange={(e) => setPassword(e.target.value)}
                                placeholder="password"
                                {...register("password", {
                                    required: "Password is required"
                                })}



                            />

                            {errors.password && (
                                <p className="error_mssg">{errors.password.message}</p>
                                )}
                        </label>



                        <label htmlFor="details-gebruikersnaam">
                            Gebruikersnaam :
                            <input
                                type="text"
                                id="username"
                                // value={username}
                                // onChange={(e) => setUsername(e.target.value)}
                                placeholder="username"
                                {...register("username", {
                                    required: "Username is required"
                                })}
                            />

                            {errors.username && (
                                <p className="error_mssg">{errors.username.message}</p>
                            )}
                        </label>


                            {/*{errors && <p className="error-msg">Registratie mislukt !!*/}
                            {/*    Alle velden invullen</p>}*/}

                        <button type="submit">Registreren</button>
                        </fieldset>
                    </form>

                    <p>Heb je al een account? Je kunt je <Link to="/Login">hier</Link> inloggen.</p>


                </section>

            </div>


        );



}

export default Subscription;


