import React, {useContext, useState} from "react";
import {Link, useNavigate} from "react-router-dom";

import axios from "axios";
import {AuthContext} from "../../context/AuthContext";
import pic from '../../../../KLEDING_BIEB/src/assets/cotton.jpg';
import Header from "../../components/header/Header";
import {useForm} from "react-hook-form";




function Subscription() {
    const {subscription} = useContext(AuthContext);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');
    const navigate = useNavigate();
    const {toggleSuccessRegister} = useState(true);
    const token = localStorage.getItem('token');
    const { formState: { errors }} = useForm();

    async function registerUser(e) {
        e.preventDefault()
        console.log(email, password, username)
        try {
            const response = await axios.post('http://localhost:8083/users', {
                email: email,
                password: password,
                username: username,
            })
            if (response.status !== 201) {
                toggleSuccessRegister(false);
            }
            console.log(response)
           //login(email);
         navigate('/Account');
        } catch (e) {
            console.error(e)
        }

    }


        return (


            <p className="page2">
                <Header icon={pic}/>


                <ul className="form-xtra">

                    <form onSubmit={registerUser}>


                        <fieldset>
                            <legend> <h1>Registreren</h1></legend>

                        <label htmlFor="details-email">
                            E-mailadres :
                            <input
                                type="text"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                placeholder="email"
                                validationRules={{
                                    required: {
                                        value: true,
                                        message: 'Dit veld is verplicht',
                                    }
                                }}
                            />
                            {errors.email && (
                                <p className="error_mssg">{errors.email.message}</p>
                            )}
                        </label>




                        <label htmlFor="details-wachtwoord">
                            Wachtwoord :
                            <input
                                type="text"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="password"
                                validationRules={{
                                    required: {
                                        value: true,
                                        message: 'Dit veld is verplicht',
                                    }
                                }}


                            />

                            {errors.password && (
                                <p className="error_mssg">{errors.password.message}</p>
                                )}
                        </label>



                        <label htmlFor="details-gebruikersnaam">
                            Gebruikersnaam :
                            <input
                                type="text"
                                id="username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                placeholder="username"
                                validationRules={{
                                    required: {
                                        value: true,
                                        message: 'Dit veld is verplicht',
                                    }
                                }}
                            />

                            {errors.username && (
                                <p className="error_mssg">{errors.username.message}</p>
                            )}
                        </label>


                            {errors && <p className="error-msg">Registratie mislukt !!
                                Alle velden invullen</p>}

                        <button type="submit">Registreren</button>
                        </fieldset>
                    </form>

                    <p>Heb je al een account? Je kunt je <Link to="/Login">hier</Link> inloggen.</p>


                </ul>

            </p>


        );



}

export default Subscription;


