import React, {useContext, useEffect, useState} from "react";
//import {Link, useNavigate} from "react-router-dom";

import axios from "axios";
import {AuthContext} from "../../context/AuthContext";
//import pic from '../../../../Kleding_Bib/src/assets/cotton.jpg';
import Header from "../../components/header/Header";
import {useForm} from "react-hook-form";
import jwtDecode from "jwt-decode";
import Button from "../../components/button/Button";



function Subscription() {

    const {
        account: {username, password, email},
        fetchUserData } = useContext(AuthContext);

    //  const url = "http://localhost:8080";
    const jwt = localStorage.getItem("token")
    const decodedToken = jwtDecode(jwt);
    decodedToken.sub = undefined;
    //decodedToken.sub = undefined;
    const {register, handleSubmit, formState: { errors }} = useForm();
    const [clicks, setClicks]= useState(0);
    const [onsubmit, toggleOnSubmit] = useState(true)

    // const refreshPage = () =>{
    //     setClicks(clicks +1 );
    //     window.location.reload();}

    useEffect(() => {
        fetchUserData(jwt, decodedToken.sub)
    }, [decodedToken.sub, jwt])


    const storedToken = localStorage.getItem( 'token' )

    const onSubmit = async (data) => {
        try {
            await axios.put(`http://localhost:8083/accounts/${username}`, data, {
                headers: {Authorization: `Bearer ${storedToken}`}})

            // refreshPage()

        } catch (error) {
            console.error(error);
            toggleOnSubmit(false)
        }
    }

    return (
        <>
            <main>

                <Header
                    title="Welcome on your profile page."
                    // backgroundImage={tarwe}
                    // classname="outer-container"
                    // id="top-section"
                />
                <div className="outer-container" id="outer-container__profile">
                    <div className="inner-container" id="inner-container__profile">

                        <section className="left-column">
                            <h2 className="column-title">Your profile settings: </h2>
                            <span>
                                <p className="profile-settings"><strong>username:</strong> {username}</p>
                                <p className="profile-settings"><strong>password:</strong> {password}</p>
                                <p className="profile-settings"><strong>Email:</strong> {email}</p>
                            </span>
                        </section>



                        <section className="column">
                            {/*// <h2 className="column-title">Change profile settings:</h2>*/}
                            <form onSubmit={handleSubmit(onSubmit)} >

                                <label htmlFor="username">
                                    <strong className="inputtTitle">Username:</strong>
                                    <input
                                        type="text"
                                        className="profile-field"
                                        id="username"
                                        placeholder="username"

                                        {...register("username", {
                                            required: {
                                                value: true
                                            }
                                        })}
                                    />
                                    {errors.username && <p className="error-mssg">username is required</p>}
                                </label>

                                <label htmlFor="password">
                                    <strong className="inputTitle">Password:</strong>
                                    <input
                                        type="text"
                                        className="profile-field"
                                        id="password"
                                        placeholder="password"

                                        {...register("password", {
                                            required: {
                                                value: true
                                            }
                                        })}
                                    />
                                    {errors.password && <p className="error-mssg">password is required</p>}
                                </label>



                                <label htmlFor="email-field">
                                    <strong className="input-field-title">Email:</strong>:
                                    <input
                                        type="text"
                                        className="profile-field"
                                        id="email"
                                        placeholder="email"
                                        {...register("email", {
                                            required: {
                                                value: true,
                                                message: "Email is required"
                                            },
                                            pattern: {
                                                value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
                                                message: "Invalid email address"
                                            }
                                        })}
                                    />
                                    {errors.email && (
                                        <p className="error-mssg">{errors.email.message}</p>
                                    )}
                                </label>

                                <div className="button">
                                    {<Button className="button"
                                             children="Update your profile"
                                             type="submit"
                                    />}
                                </div>

                                {!onsubmit &&
                                    <h2 className="warning-msg">Something went wrong contact your Admin</h2>}

                            </form>
                        </section>
                    </div>
                </div>


                />
            </main>
        </>
    );
}


export default Subscription;


















