function Subscription() {
   const {login} = useContext(AuthContext);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');
   const navigate = useNavigate();
   const {toggleSuccessRegister} = useState(true);
    const token = localStorage.getItem('token');
  //  const { formState: { errors }} = useForm();
   //const [errors, toggleErrors] = useState(false);
   const [onsubmit, toggleOnSubmit] = useState(true);
    const {register, handleSubmit, formState: { errors },setError} = useForm();

    //const storedToken = localStorage.getItem( 'token' )


    // async function registerUser() {
    //     try {
    //
    //         const response = await axios.post('http://localhost:8083/users',{
    //             email: email,
    //             password: password,
    //             username: username,
    //     })
    //
    //
    //         if (response.status !== 201) {
    //             toggleSuccessRegister(false);
    //         }
    //         console.log(response);
    //         //login(data.email);
    //         navigate('/Account');
    //     } catch (e) {
    //         console.error(e);
    //         setError("register", {
    //             type: "manual",
    //             message: "Registration failed. Please check your details and try again."
    //         });
    //     }
    // }


    const onSubmit = async () => {
        try {

            const response = await axios.post('http://localhost:8083/users', {
                email: email,
                password: password,
                username: username,
            });

            if (response.status === 201) {
                // Registratie succesvol (statuscode 201)
                // Voer hier eventuele andere acties uit die je wilt ondernemen na succesvolle registratie

                // Navigeer bijvoorbeeld naar een andere pagina
                navigate('/Login');
            } else {
                // Er is iets misgegaan, toon een foutmelding of handel de fout op een andere manier af
                console.log('Er is iets misgegaan bij het registreren');
            }
        } catch (error) {
            // Er is een fout opgetreden bij het maken van het verzoek
            console.error(error);
            toggleOnSubmit(false);
        }
    };




        return (


            <div className="page2">
                <Header icon={pic}/>


                <section className="form-xtra">

                    {/*<form onSubmit={handleSubmit(registerUser)}>*/}
                    <form onSubmit={handleSubmit(onSubmit)}>

                        <fieldset>
                            <legend> <h1>Registreren</h1></legend>

                        <label htmlFor="details-email">
                            E-mailadres :
                            <input
                                type="text"
                                id="email"
                                //value={email}
                                // onChange={(e) => setEmail(e.target.value)}
                                placeholder="email"
                                {...register("email", {
                                    required: "Email is required",
                                    pattern: {
                                        value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
                                        message: "Invalid email address"
                                    }
                                })}
                            />
                            {errors.email && (
                                <p className="error_mssg">{errors.email.message}</p>
                            )}
                        </label>




                        <label htmlFor="details-wachtwoord">
                            Wachtwoord :
                            <input
                                type="text"
                                id="password"
                                //value={password}
                                 //onChange={(e) => setPassword(e.target.value)}
                                placeholder="password"
                                {...register("password", {
                                    required: "Password is required"
                                })}

                            />

                            {errors.password && (
                                <p className="error_mssg">{errors.password.message}</p>
                                )}
                        </label>



                        <label htmlFor="details-gebruikersnaam">
                            Gebruikersnaam :
                            <input
                                type="text"
                                id="username"
                                //value={username}
                                //onChange={(e) => setUsername(e.target.value)}
                                placeholder="username"
                                {...register("username", {
                                    required: "Username is required"
                                })}
                            />

                            {errors.username && (
                                <p className="error_mssg">{errors.username.message}</p>
                            )}
                        </label>


                            {/*{errors.register && <p className="error-msg">{errors.register.message} Registratie mislukt !!*/}
                            {/*    Alle velden invullen</p>}*/}

                        <button type="submit">Registreren</button>
                        </fieldset>
                    </form>

                    <p>Heb je al een account? Je kunt je <Link to="/Login">hier</Link> inloggen.</p>


                </section>

            </div>


        );



}

export default Subscription;


